  const [chatroom, setChatroom] = useState('default');
  const [chatroomsWidth, setChatroomsWidth] = useState(document.getElementById('chatroomsContainer'));

  useEffect(() => {
    console.log(chatroomsWidth)
  }, [chatroomsWidth])

  return (
    <AppContainer>
      <Header />
      <Chatrooms />
      <Chat />
