import React, {useState, useEffect} from 'react'
import styled from 'styled-components';

import './App.css';
import Header from './components/Header';
import Chatrooms from './components/Chatrooms';
import Chat from './components/Chat';

function App() {
  const [chatroom, setChatroom] = useState('default');
  const [chatroomsWidth, setChatroomsWidth] = useState('468px');
  console.log(chatroomsWidth);

  const resizeObserver = new ResizeObserver(() => {
      setChatroomsWidth(document.getElementById('chatroomsContainer').offsetWidth)
  });

  useEffect(() => {
      resizeObserver.observe(document.getElementById('chatroomsContainer'))

      // return () => {
      //   resizeObserver.unobserve(document.getElementById('chatroomsContainer'));
      // }

  }, [])

  return (
    <AppContainer>
      <Header />
      <Chatrooms chatroomsWidth={chatroomsWidth} />
      <Chat chatroomsWidth={chatroomsWidth}/>
    </AppContainer>
  );
}

export default App;

const AppContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  background-color: black;
  width: 100%;
  min-height: 100vh;
`
