import React, {useState, useEffect} from 'react'
import styled from 'styled-components';
import Split from 'react-split';

import './App.css';
import Header from './components/Header';
import Chatrooms from './components/Chatrooms/Chatrooms';
import Chat from './components/Chat/Chat';
import CreateModal from './components/Chatrooms/CreateModal';

function App() {
  const [activeRoom, setActiveRoom] = useState('default');
  const [chatrooms, setChatrooms] = useState(['default', 'room 1', 'room 2']);

  const [displayCreateModal, setDisplayCreateModal] = useState(false);

  // const [chatroomsWidth, setChatroomsWidth] = useState(null);


  // // RESIZING CHATROOMS (WIDTH)
  // const chatroomsResizeObserver = new ResizeObserver(() => {
  //     setChatroomsWidth(document.getElementById('chatroomsContainer').offsetWidth)
  // });
  // useEffect(() => {
  //     chatroomsResizeObserver.observe(document.getElementById('chatroomsContainer'))
  //     return () => {
  //       chatroomsResizeObserver.unobserve(document.getElementById('chatroomsContainer'));
  //     }
  // }, [])



  return (
    <AppContainer>
      <Header />
      <SplitContainer sizes={[15, 85]}>
        <Chatrooms
          chatrooms={chatrooms}
          setActiveRoom={setActiveRoom}
          activeRoom={activeRoom}
          setDisplayCreateModal={() => {setDisplayCreateModal(!displayCreateModal)}}
        />
        <Chat
          activeRoom={activeRoom}
        />
      </SplitContainer>
      {displayCreateModal ?
      <CreateModal
        setChatrooms={setChatrooms}
        chatrooms={chatrooms}
        setDisplayCreateModal={setDisplayCreateModal}
      /> : null}
    </AppContainer>
  );
}

export default App;

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  background-color: black;
  width: 100%;
  min-height: 100vh;
  position: relative;
`

const SplitContainer = styled(Split)`
  display: flex;
  width: 100%;
`
