import React, {useState, useEffect} from 'react'
import openSocket from 'socket.io-client';
import styled from 'styled-components';
import Split from 'react-split';

import './App.css';
import Header from './components/Header';
import Chatrooms from './components/Chatrooms/Chatrooms';
import Chat from './components/Chat/Chat';
import CreateModal from './components/Chatrooms/CreateModal';

function App() {
  const [activeRoom, setActiveRoom] = useState();
  const [messages, setMessages] = useState([]);
  const [chatrooms, setChatrooms] = useState([]);

  const [displayCreateModal, setDisplayCreateModal] = useState(false);


  useEffect(() => {
    const socket = openSocket('http://localhost:8080');

    // retrieve and set chatrooms from server
    socket.on('initializing chat', data => {
        setActiveRoom(data.rooms[0]);
        setChatrooms(data.rooms);
        setMessages(data.messages);
    })

    // listen for newly created rooms
    socket.on('new room', data => {
        setChatrooms(data.rooms);
    })

    // listen for newly created messages
    socket.on('new message', data => {
      setMessages(data.messages);
    })

    // listen for room change
    socket.on('room change', data => {
        console.log(data);
    })
  }, [])





  return (
    <AppContainer>
      <Header />
      <SplitContainer sizes={[15, 85]} minSize={[140, 800]} gutterSize={4}>
        <Chatrooms
          chatrooms={chatrooms}
          setActiveRoom={setActiveRoom}
          activeRoom={activeRoom}
          setDisplayCreateModal={() => {setDisplayCreateModal(!displayCreateModal)}}
        />
        <Chat
          activeRoom={activeRoom}
          messages={messages}
        />
      </SplitContainer>
      {displayCreateModal ?
      <CreateModal
        setChatrooms={setChatrooms}
        chatrooms={chatrooms}
        setDisplayCreateModal={setDisplayCreateModal}
      /> : null}
    </AppContainer>
  );
}

export default App;

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  background-color: black;
  width: 100%;
  min-height: 100vh;
  position: relative;
`

const SplitContainer = styled(Split)`
  display: flex;
  width: 100%;

  & .gutter {
    background-color: #202020;

    &:hover {
      cursor: col-resize;
    }
  }
`
