import React, {useState, useEffect} from 'react'
import styled from 'styled-components'

import Keyboard from './Keyboard';


const Chat = (props) => {
    // keeps track of same sender having consecutive messages
    const [sameSender, setSameSender] = useState(false);

    return (
        <ChatContainer>
            <ChatArea>
                {props.messages.map(msg => {
                    if (props.activeRoom.id === msg.roomId) {
                        return (
                            <MessageDiv>
                                {!sameSender ?
                                    <SenderInfo myMessage={msg.userId === props.user.id ? true : false}>{msg.userId}</SenderInfo>
                                : null}
                                {msg.userId === props.user.id ? 
                                    <MyMessage>{msg.text}</MyMessage>
                                :   <TheirMessage>{msg.text}</TheirMessage>
                                }
                            </MessageDiv>
                        )
                    }
                })}
            </ChatArea>
            <Keyboard
                messages={props.messages}
                setMessages={props.setMessages}
                activeRoom={props.activeRoom}
                user={props.user}
            />
        </ChatContainer>
    )
}

export default Chat;

const ChatContainer = styled.div`
    box-sizing: border-box;
    width: calc(100vw - 10rem);
    height: auto;
    display: flex;
    flex-direction: column;
`

const ChatArea = styled.div`
    height: calc(100% - 6rem);
    overflow: auto;
    width: 100%;
    box-sizing: border-box;
    border: 1px solid black;
    border-left: none;
    background-color: black;
    color: white;
    padding: 1rem 0;
`

const MessageDiv = styled.div`
    display: flex;
    margin: 0.2rem 2rem;
    flex-direction: column;
`

const SenderInfo = styled.p`
    padding: 0;
    margin: 0;
    color: gray;
    font-size: 0.8rem;
    align-self: ${props => props.myMessage ? 'flex-end' : 'flex-start'};

`

const MyMessage = styled.div`
    margin-left: auto;
    background-color: #147efb;
    padding: 0.7rem;
    border-radius: 24px;
`

const TheirMessage = styled.div`
    width: auto;
    background-color: #333333;
    padding: 0.7rem;
    border-radius: 24px;
    margin-right: auto;
`
