import React from 'react'
import styled from 'styled-components'

import ChatArea from './ChatArea';
import Keyboard from './Keyboard';

const Chat = (props) => {

    return (
        <ChatContainer chatroomsWidth={props.chatroomsWidth}>
            <ChatArea keyboardHeight={props.keyboardHeight} />
            <Keyboard />
        </ChatContainer>
    )
}

export default Chat;

const ChatContainer = styled.div`
    box-sizing: border-box;
    width: calc(100vw - ${props => props.chatroomsWidth}px);
    height: calc(100vh - 6rem);
    display: flex;
    flex-direction: column;
`
