import React, {useState, useEffect} from 'react'
import styled from 'styled-components'

import Keyboard from './Keyboard';


const Chat = (props) => {
    const [roomMessages, setRoomMessages] = useState([]);

    // filter out messages to specific room
    useEffect(() => {
        console.log(props.messages);
        setRoomMessages(props.messages.filter(msg => msg.roomId === props.activeRoom.id))
    }, []);

    useEffect(() => {
        console.log(roomMessages);
    }, [roomMessages])

    return (
        <ChatContainer>
            <ChatArea>
                {props.messages.map((msg, index) => {
                    if (props.activeRoom.id === msg.roomId) {
                        return (
                            <MessageDiv>
                                {/* dont display sender info in any consecutive messages! (only first) */}
                                <SenderInfo
                                    myMessage={msg.userId === props.user.id ? true : false}
                                    display={
                                        (index !== 0) &&
                                        (msg.userId === props.messages[index - 1].userId)
                                        ? false : true
                                    }
                                >
                                    {msg.username} | {msg.time}
                                </SenderInfo>
                                {msg.userId === props.user.id ? 
                                    <MyMessage>{msg.text}</MyMessage>
                                :   <TheirMessage>{msg.text}</TheirMessage>
                                }
                            </MessageDiv>
                        )
                    }
                })}
            </ChatArea>
            <Keyboard
                darkMode={props.darkMode}
                messages={props.messages}
                setMessages={props.setMessages}
                activeRoom={props.activeRoom}
                user={props.user}
            />
        </ChatContainer>
    )
}

export default Chat;

const ChatContainer = styled.div`
    box-sizing: border-box;
    width: 100%;
    height: auto;
    display: flex;
    flex-direction: column;
    padding: 1rem 0;
    border-radius: 60px;
`

/*     height: calc(100% - 6rem); */
const ChatArea = styled.div`
    height: 100%;
    overflow: auto;
    width: 100%;
    box-sizing: border-box;
    border-left: none;
    color: white;
`

const MessageDiv = styled.div`
    display: flex;
    margin: 0.2rem 2rem;
    flex-direction: column;
`

const SenderInfo = styled.p`
    padding: 0;
    margin: 0;
    color: gray;
    font-size: 0.8rem;
    align-self: ${props => props.myMessage ? 'flex-end' : 'flex-start'};
    display: ${props => props.display ? 'flex' : 'none'}
`

const MyMessage = styled.div`
    margin-left: auto;
    background-color: #147efb;
    padding: 0.7rem;
    border-radius: 24px;
`

const TheirMessage = styled.div`
    width: auto;
    background-color: #333333;
    padding: 0.7rem;
    border-radius: 24px;
    margin-right: auto;
`
