import React, {useState, useEffect} from 'react'
import styled from 'styled-components'

const Layout = (props) => {
    const [darkMode, setDarkMode] = useState(false);

    useEffect(() => {
        console.log(darkMode);
    }, [darkMode])
    return (
        <LayoutContainer darkMode={darkMode}>
            <ModeContainer onClick={() => setDarkMode(!darkMode)}>
                {darkMode ? 'Light' : 'Dark'}
            </ModeContainer>
            {props.children}
            <CircleOne />
            <CircleTwo />
        </LayoutContainer>
    )
}

export default Layout;

const ModeContainer = styled.button`
  position: absolute;
  z-index: 1;
  top: 2%;
  left: 4%;
  width: 6rem;
  height: 3rem;
  background: white;
  border: none;
  display: flex;
  justify-content: center;
`

const LayoutContainer = styled.div`
    width: 100%;
    min-height: 100vh;
    display: flex;
    background: linear-gradient(to right top, #65fdc9, #6cdbeb);
`

const CircleOne = styled.div`
    height: 30rem;
    width: 30rem;
    position: absolute;
    background: white;
    top: 2%; 
    left: 7%;
    z-index: 0;
    background-color: red;
    background: linear-gradient(to right bottom, rgba(130, 0, 205, 1) 80%, rgba(255, 0, 0, 0.5));
    border-radius: 50%;
`

const CircleTwo = styled.div`
    height: 26rem;
    width: 26rem;
    position: absolute;
    background: white;
    bottom: 8%; 
    right: 2%;
    z-index: 0;
    border-radius: 50%;
    background: linear-gradient(to left top, rgba(80, 40, 240, 0.9) 80%, rgba(255, 255, 255, 0.1));
`
