import React, {useState, useEffect} from 'react'
import styled from 'styled-components';

import './App.css';
import Header from './components/Header';
import Chatrooms from './components/Chatrooms/Chatrooms';
import Chat from './components/Chat/Chat';

function App() {
  const [activeChat, setActiveChat] = useState('default');
  const [chatrooms, setChatrooms] = useState(['default', 'room 1', 'room 2']);

  const [displayCreateModal, setDisplayCreateModal] = useState(false);

  const [chatroomsWidth, setChatroomsWidth] = useState(null);
  const [keyboardHeight, setKeyboardHeight] = useState(null);


  // RESIZING BOTH CHATROOMS (WIDTH) AND KEYBOARD (HEIGHT)
  // find a way to factor this code??
  const chatroomsResizeObserver = new ResizeObserver(() => {
      setChatroomsWidth(document.getElementById('chatroomsContainer').offsetWidth)
  });
  const keyboardResizeObserver = new ResizeObserver(() => {
      setKeyboardHeight(document.getElementById('keyboardContainer').offsetHeight);
  })

  useEffect(() => {
      chatroomsResizeObserver.observe(document.getElementById('chatroomsContainer'))
      return () => {
        chatroomsResizeObserver.unobserve(document.getElementById('chatroomsContainer'));
      }
  }, [])

  useEffect(() => {
      keyboardResizeObserver.observe(document.getElementById('keyboardContainer'));
      return () => {
        keyboardResizeObserver.unobserve(document.getElementById('keyboardContainer'));
      }
  })


  return (
    <AppContainer>
      <Header />
      <Chatrooms chatroomsWidth={chatroomsWidth} chatrooms={chatrooms} setActiveChat={setActiveChat}/>
      <Chat chatroomsWidth={chatroomsWidth} activeChat={activeChat} keyboardHeight={keyboardHeight}/>
    </AppContainer>
  );
}

export default App;

const AppContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  background-color: black;
  width: 100%;
  min-height: 100vh;
`
