import React, {useState, useEffect} from 'react'
import styled from 'styled-components';
import openSocket from 'socket.io-client';

const Keyboard = (props) => {
    const [message, setMessage] = useState('');


    const sendMessageHandler = () => {
        console.log(message);

        const socket = openSocket('http://localhost:8080');
        socket.emit('new message', {
            text: message,
            userId: '1',
            roomId: props.activeRoom.id,
            time: Date.now()
        })

        // props.setMessages([
        //     ...props.messages, 
        //     {text: message, me: true, room: props.activeRoom, user: 'tom'}
        // ]);
        setMessage('');
    }

    return (
        <KeyboardContainer>
            <KeyboardDiv>
                <KeyboardInput type="textarea" value={message} onChange={(e) => setMessage(e.target.value)} />
            </KeyboardDiv>
            <SendDiv>
                <SendButton onClick={() => sendMessageHandler()}>Send</SendButton>
            </SendDiv>
        </KeyboardContainer>
    )
}

export default Keyboard;

const KeyboardContainer = styled.div`
    box-sizing: border-box;
    width: 100%;
    height: 4rem;
    background-color: black;
    border: 1px solid black;
    border-left: none;
    border-top: none;
    display: flex;
    margin-top: 1rem;
`

const KeyboardDiv = styled.div`
    height: 100%;
    width: 93%;
    padding: 0 1rem;
`
const SendDiv = styled.div`
    height: 100%;
    bottom: 0;
    width: 7%;
    padding-right: 1rem;
`

const KeyboardInput = styled.input`
    height: 100%;
    border-radius: 50px;
    font-size: 24px;
    box-sizing: border-box;
    border: 1px solid gray;
    background-color: black;
    color: white;
    width: 100%;
    padding: 0 1.5rem;
`

const SendButton = styled.button`
    height: 100%;
    box-sizing: border-box;
    border-radius: 50px;
    width: 100%;
    border: none;
    background-color: #147efb;
    color: white;
    font-size: 1rem;

    &:hover {
        cursor: pointer;
    }
`
