import React, {useState} from 'react'
import styled from 'styled-components'

const Layout = (props) => {
    return (
        <LayoutContainer darkMode={props.darkMode}>
            {props.children}
            <CircleOne darkMode={props.darkMode}/>
            <CircleTwo darkMode={props.darkMode}/>
        </LayoutContainer>
    )
}

export default Layout;


const LayoutContainer = styled.div`
    width: 100%;
    min-height: 100vh;
    display: flex;
    background: ${props => (props.darkMode ?
        'linear-gradient(to right top, rgba(39, 27, 32, 1), rgba(35, 17, 22, 1));' :
        'linear-gradient(to right top, rgba(169, 255, 247, 0.9), rgba(152, 255, 204, 0.8));'
        )};
`

const CircleOne = styled.div`
    height: ${props => (props.darkMode ? '30rem' : '26rem')};
    width: ${props => (props.darkMode ? '30rem' : '26rem')};
    position: absolute;
    top: 2%; 
    left: 7%;
    z-index: 0;
    background: ${props => (props.darkMode ?
        'gray' :
        'linear-gradient(to right bottom, rgba(55, 57, 58, 0.8), rgba(191, 182, 187, 1))'
        )};
    border-radius: 50%;
    transition: ease-out 1.5s;
`

const CircleTwo = styled.div`
    height: ${props => (props.darkMode ? '26rem' : '30rem')};
    width: ${props => (props.darkMode ? '26rem' : '30rem')};
    position: absolute;
    bottom: 8%; 
    right: 2%;
    z-index: 0;
    border-radius: 50%;
    background: ${props => (props.darkMode ?
        'gray':
        'linear-gradient(to right bottom, rgba(130, 0, 205, 1) 80%, rgba(255, 0, 0, 0.5))'
    )};
    transition: ease-out 1.5s;

`
