import React, {useState, useEffect} from 'react'
import styled from 'styled-components'

import ChatArea from './ChatArea';
import Keyboard from './Keyboard';

const Chat = (props) => {
    const [messages, setMessages] = useState([{text: 'hey!', room: 'default', user: 'tom', me: true}, {text: 'hello', room: 'default', user: 'max', me: false}]);

    useEffect(() => {
        console.log(messages);
    }, [messages])

    return (
        <ChatContainer chatroomsWidth={props.chatroomsWidth}>
            <ChatArea
                keyboardHeight={props.keyboardHeight}
                messages={messages}
            />
            <Keyboard
                messages={messages}
                setMessages={setMessages}
            />
        </ChatContainer>
    )
}

export default Chat;

const ChatContainer = styled.div`
    box-sizing: border-box;
    width: calc(100vw - ${props => props.chatroomsWidth}px);
    height: calc(100vh - 6rem);
    display: flex;
    flex-direction: column;
`
