import React, {useState, useEffect} from 'react'
import openSocket from 'socket.io-client';
import styled from 'styled-components';
import Split from 'react-split';

import './App.css';
import Layout from './components/Layout';
import CreateAccount from './components/CreateAccount';
import Chatrooms from './components/Chatrooms/Chatrooms';
import Chat from './components/Chat/Chat';
import CreateModal from './components/Chatrooms/CreateModal';

// ADD LOADING COMPONENT

function App(props) {
  const [user, setUser] = useState(null);
  const [activeRoom, setActiveRoom] = useState();
  const [messages, setMessages] = useState([]);
  const [chatrooms, setChatrooms] = useState([]);
  const [darkMode, setDarkMode] = useState(false);

  const [displayCreateModal, setDisplayCreateModal] = useState(false);

  useEffect(() => {
    const socket = openSocket('http://localhost:8080');

    // retrieve user info, if they have been here before
    socket.emit('initialize', {
      username: localStorage.getItem('username')
    })

    // retrieve and set chatrooms from server
    socket.on('initialize', data => {
        setUser(data.user);
        setActiveRoom(data.rooms[0]);
        setChatrooms(data.rooms);
        setMessages(data.messages);
    })

    // listen for newly created rooms
    socket.on('new room', data => {
        setChatrooms(data.rooms);
    })

    // listen for newly created messages
    socket.on('new message', data => {
        setMessages(data.messages);
    })

  }, [])

  // useEffect(() => {
  //   console.log(props.darkMode);
  // }, [props.darkMode])
  console.log(props);

  return (
    <Layout darkMode={darkMode}>
      <ModeContainer onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? 'Light' : 'Dark'}
      </ModeContainer>
      <AppContainer darkMode={darkMode}>
        {!user ? 
          <CreateAccount setUser={setUser} />
        :
          <>
            <SplitContainer sizes={[15, 85]} minSize={[140, 800]} gutterSize={4} darkMode={darkMode}>
              <Chatrooms
                darkMode={darkMode}
                chatrooms={chatrooms}
                setActiveRoom={setActiveRoom}
                activeRoom={activeRoom}
                setDisplayCreateModal={() => {setDisplayCreateModal(!displayCreateModal)}}
              />
              <Chat
                darkMode={darkMode}
                activeRoom={activeRoom}
                messages={messages}
                user={user}
              />
            </SplitContainer>
            {displayCreateModal ?
            <CreateModal
              setChatrooms={setChatrooms}
              chatrooms={chatrooms}
              setDisplayCreateModal={setDisplayCreateModal}
            /> : null}
          </>
        }

      </AppContainer>
    </Layout>
  );
}

export default App;

const ModeContainer = styled.button`
  position: absolute;
  z-index: 1;
  top: 2%;
  left: 4%;
  width: 6rem;
  height: 3rem;
  background: white;
  border: none;
  display: flex;
  justify-content: center;
  background: linear-gradient(to bottom right, white 60%, gray);
  border-radius: 20px;
`

const AppContainer = styled.div`
  box-sizing: border-box;
  border-radius: 20px;
  overflow: hidden;
  width: 80rem;
  height: 46rem;
  margin: auto;
  position: relative;
  background: ${(props) => (props.darkMode
    ? 'linear-gradient(to right bottom, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0.5));'
    : 'linear-gradient(to right bottom, rgba(255, 255, 255, 1), rgba(255, 255, 255, 0.3));'
  )};
  border: ${(props => (props.darkMode ? '0.01rem solid black' : '0.01rem solid white'))};
  backdrop-filter: blur(0.5rem);
  z-index: 1;
`

const SplitContainer = styled(Split)`
  display: flex;
  width: 100%;

  & .gutter {
    background-color: ${(props => (props.darkMode ? 'black' : 'white'))};

    &:hover {
      cursor: col-resize;
    }
  }
`
