import React, {useState, useEffect} from 'react'
import styled from 'styled-components';
import openSocket from 'socket.io-client';

const Keyboard = (props) => {
    const [message, setMessage] = useState('');

    const sendMessageHandler = () => {
        const socket = openSocket('http://localhost:8080');
        socket.emit('new message', {
            text: message,
            userId: props.user.id,
            roomId: props.activeRoom.id,
            time: new Date().toISOString().slice(0, 19).replace('T', ' '),
            username: props.user.username
        });
        setMessage('');
    }

    return (
        <KeyboardContainer>
            <KeyboardDiv>
                <KeyboardInput darkMode={props.darkMode} type="textarea" value={message} onChange={(e) => setMessage(e.target.value)} />
            </KeyboardDiv>
            <SendDiv>
                <SendButton onClick={() => sendMessageHandler()}><i className="fas fa-arrow-up fa-2xl"></i></SendButton>
            </SendDiv>
        </KeyboardContainer>
    )
}

export default Keyboard;

const KeyboardContainer = styled.div`
    box-sizing: border-box;
    width: 100%;
    height: 4rem;
    border-left: none;
    border-top: none;
    display: flex;
`

const KeyboardDiv = styled.div`
    height: 100%;
    width: 93%;
    padding: 0 1rem;
`
const SendDiv = styled.div`
    height: 100%;
    bottom: 0;
    width: 7%;
    padding-right: 1rem;
`

const KeyboardInput = styled.input`
    height: 100%;
    border-radius: 50px;
    font-size: 24px;
    box-sizing: border-box;
    border: 1px solid gray;
    color: white;
    width: 100%;
    padding: 0 1.5rem;
    background-color: ${props => props.darkMode ? 'black' : 'white'};
`

const SendButton = styled.button`
    height: 100%;
    box-sizing: border-box;
    border-radius: 50px;
    width: 100%;
    border: none;
    background-color: #147efb;
    color: white;
    font-size: 1rem;

    &:hover {
        cursor: pointer;
    }
`
