import React, {useState} from 'react'
import styled from 'styled-components';

import Room from './Room';


const Chatrooms = (props) => {

    var p = document.getElementById('chatroomsContainer');

p.addEventListener('click', function init() {
    p.removeEventListener('click', init, false);
    p.className = p.className + ' resizable';
    var resizer = document.createElement(<Resizer />);
    resizer.className = 'resizer';
    p.appendChild(resizer);
    resizer.addEventListener('mousedown', initDrag, false);
}, false);

var startX, startY, startWidth, startHeight;

function initDrag(e) {
   startX = e.clientX;
   startY = e.clientY;
   startWidth = parseInt(document.defaultView.getComputedStyle(p).width, 10);
   startHeight = parseInt(document.defaultView.getComputedStyle(p).height, 10);
   document.documentElement.addEventListener('mousemove', doDrag, false);
   document.documentElement.addEventListener('mouseup', stopDrag, false);
}

function doDrag(e) {
   p.style.width = (startWidth + e.clientX - startX) + 'px';
   p.style.height = (startHeight + e.clientY - startY) + 'px';
}

function stopDrag(e) {
    document.documentElement.removeEventListener('mousemove', doDrag, false);    document.documentElement.removeEventListener('mouseup', stopDrag, false);
}

    return (
        <ChatroomsContainer width={props.chatroomsWidth} id="chatroomsContainer">
            <RoomsContainer>
                {props.chatrooms.map(room => {
                    return <Room name={room} setActiveRoom={props.setActiveRoom} activeRoom={props.activeRoom}/>
                })}
            </RoomsContainer>
            <CreateContainer>
                <button onClick={props.setDisplayCreateModal}>Create new room</button>
            </CreateContainer>
        </ChatroomsContainer>
    )
}

export default Chatrooms;

const ChatroomsContainer = styled.div`
    box-sizing: border-box;
    min-height: calc(100vh - 6rem);
    background-color: gray;
    resize: horizontal;
    overflow: auto;
    width: 14rem;
    display: flex;
    flex-direction: column;
    border: 1px solid black;
    position: relative;
`

const RoomsContainer = styled.div`
    height: calc(100% - 6rem);
`

const CreateContainer = styled.div`
    box-sizing: border-box;
    border-top: 1px solid black;
    width: 100%;
    background-color: gray;
    height: 6rem;
`

const Resizer = styled.div`
    width: 10px;
    height: 10px;
    background: blue;
    position:absolute;
    right: 0;
    top: 0;
    cursor: se-resize;
`
